then
  echo "-----------------------------------------------------------------------------------------------------"
  echo " ERROR WHILE RUNNING SCRIPT "
  echo "-----------------------------------------------------------------------------------------------------"
  echo " ${V_ERROR_STRING}"
  echo "-----------------------------------------------------------------------------------------------------"
  echo " Code...: ${V_ERROR_CODE}"
  echo "-----------------------------------------------------------------------------------------------------"
  return ${V_ERROR_CODE}
fi
}
