INST_NAME=`${ECHOCMD} ${v_host_inst} | ${SEDCMD} 's/=/ /g' | ${AWKCMD} '{print $2}'`;
V_REPLACE=`${ECHOCMD} ${V_REPLACE} | ${SEDCMD} -e 's/'${HOST_NAME}'/'${HOST_NAME}' \('${INST_NAME}'\)/g'`;
