  , a.index_type
  , a.status
  , (select count(*) from dba_indexes@SOURCE_DB where owner = a.owner and index_type = a.index_type) "QTD SOURCE"
  , (select count(*) from dba_indexes where owner = a.owner and index_type = a.index_type) "QTD TARGET"
