  TO_CHAR(SUM(DECODE(SUBSTR(TO_CHAR(FIRST_TIME, 'MM/DD/YYYY HH24:MI:SS'),10,2),'00',1,0)),'9999') "00", 
  TO_CHAR(SUM(DECODE(SUBSTR(TO_CHAR(FIRST_TIME, 'MM/DD/YYYY HH24:MI:SS'),10,2),'01',1,0)),'9999') "01", 
