         'Shared Pool','SPool'),
         'Hit Ratio','HitRtio'),
         'Global Cache','GC'),
         'Total','Tot'),
         'Database','DB'),
         'Enqueue','Enq'),
         'Percentage','%'),
         'per Second','/s'),
         'Physical','Phy'),
         'Workload','Wrklod'),
         'Capture','Cptur'),
         'Execute','Exec'),
         'Consistent','Consist'),
         'parallelized','PX'),
         'downgraded','\/'),
         'Records','Recrds'),
         'Requests','Reqs'),
         'Block','Blk'),
         'Interconnect','Interc'),
         'statements','stmts'),
         'Bytes','Byts') Metric_name, Value
        FROM (select metric_id, METRIC_NAME, INST_ID || ':' || ROUND(VALUE,1) VALUE FROM GV\$SYSMETRIC WHERE GROUP_ID = 2 and metric_id in ( nvl(p_sysmetric_id1,2144),nvl(p_sysmetric_id2,2008),nvl(p_sysmetric_id3,2010))))
