---------------Content------------------------------------------------------------------------------------------
select '<!-- Time: ' || to_char(sysdate,'hh24:mi:ss') || ' - since start: ' || to_char((sysdate - to_date(:starttime, 'dd/mm/yyyy hh24:mi:ss'))*86400, '999g999d0') || 's -->' from dual;
select '<h1>Ribas - Database Maintenance Report</h1><br>' FROM dual;
select '<a name="options"><h3>DB Options</h3></a><br>' FROM dual;
select '<table>
  <tr>
  <th>Instance</th>
  <th>DB Name</th>
  <th>DB Uniq Name</th>
  <th>Service Names</th>
  <th>Host</th>
  <th>Start Time</th>
  <th>Uptime</th>
  <th>Open mode</th>
  <th>DB Role</th>
  <th>LogMode</th>
  <th>Cluster</th>
  <th>Guard Status</th>
  <th>DataGuard</th>
  <th>Protection Level</th>
  <th>Force Logging</th>
  <th>Flashback ON</th>
