  || case when pct_waited >= 0.5 then 'critical' when pct_waited >= 0.2 then 'warning' end || '">' 
  || to_char(round(pct_waited*100,1), '999D0') || '</td></tr>'
from (select WAIT_CLASS
        , TOTAL_WAITS
