		, TIME_WAITED / sum(time_waited) over () pct_waited
	  from (select WAIT_CLASS, sum(TOTAL_WAITS) TOTAL_WAITS, round(sum(TIME_WAITED)/100) AS TIME_WAITED from GV\$SYSTEM_WAIT_CLASS group by WAIT_CLASS)
	  order by 3 desc)
