  , d.sid
  , d.serial#
  , d.osuser
  , d.process
  , c.SPID
FROM apps.fnd_concurrent_requests a,
apps.fnd_concurrent_processes b,
v\$process c,
v\$session d
WHERE a.controlling_manager = b.concurrent_process_id
